var fs = require('fs'),
    stat = fs.stat;

/*
 * å¤å¶ç®å½ä¸­çæææä»¶åæ¬å­ç®å½
 * @param{ String } éè¦å¤å¶çç®å½
 * @param{ String } å¤å¶å°æå®çç®å½
 */
var copy = function(src, dst) {
    // è¯»åç®å½ä¸­çæææä»¶/ç®å½
    fs.readdir(src, function(err, paths) {
        if (err) {
            throw err;
        }

        paths.forEach(function(path) {
            var _src = src + '/' + path,
                _dst = dst + '/' + path,
                readable, writable;

            stat(_src, function(err, st) {
                if (err) {
                    throw err;
                }

                // å¤æ­æ¯å¦ä¸ºæä»¶
                if (st.isFile()) {
                    // åå»ºè¯»åæµ
                    readable = fs.createReadStream(_src);
                    // åå»ºåå¥æµ
                    writable = fs.createWriteStream(_dst);
                    // éè¿ç®¡éæ¥ä¼ è¾æµ
                    readable.pipe(writable);
                }
                // å¦ææ¯ç®å½åéå½è°ç¨èªèº«
                else if (st.isDirectory()) {
                    exists(_src, _dst, copy);
                }
            });
        });
    });
};

// å¨å¤å¶ç®å½åéè¦å¤æ­è¯¥ç®å½æ¯å¦å­å¨ï¼ä¸å­å¨éè¦ååå»ºç®å½
var exists = function(src, dst, callback) {
    fs.exists(dst, function(exists) {
        // å·²å­å¨
        if (exists) {
            callback(src, dst);
        }
        // ä¸å­å¨
        else {
            fs.mkdir(dst, function() {
                callback(src, dst);
            });
        }
    });
};

// å¤å¶ç®å½
exists('./node_modules/vue-init/template', './', copy);